document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("[open-modal]").forEach(btn=>{btn.addEventListener("click",()=>{let modal=document.getElementById(btn.dataset.modal);window.onclick=e=>{e.target==modal&&(modal.style.display="none")},"block"===window.getComputedStyle(modal).getPropertyValue("display")?modal.style.display="none":modal.style.display="block"})}),document.querySelectorAll(".modal-close-btn").forEach(btn=>{btn.addEventListener("click",()=>{document.getElementById(btn.dataset.modal).style.display="none"})})});