document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".tab").forEach(tab=>{tab.childNodes.forEach(btn=>{btn.classList&&btn.classList.contains("tab-btn")&&btn.addEventListener("click",()=>{document.querySelectorAll(".tab-content").forEach(content=>{content.parentElement.id===tab.dataset.tab&&(content.style.display="none")}),document.getElementById(btn.dataset.content).style.display="block"})})})});